packet = ""

currentmess = ""
packet.split("").each_with_index do |char, index|
  currentmess << char
  if currentmess.length == 14
    dude = currentmess.split("")
    if dude.uniq.count < 14
      currentmess = currentmess[1,13]
    else
      puts "found: #{index} | currentmess: #{currentmess}"
      break
    end
  end
end
